module Main where
import Data.Char (isDigit, digitToInt, intToDigit)

ll = 
  [
  "",
  "",
  "",
  "",
  ""
    ]

moveIfPossible n c = case c of
  'R' -> if n`elem`"149CD" then n else 
    if isDigit n then intToDigit $ digitToInt n + 1 else 
    if n=='A' then 'B' else 'C'
  'U' -> if n`elem`"12459" then n else
    if isDigit n then 
      let m = digitToInt n in if m==3 then '1' else intToDigit $ m-4
      else case n of 
        'A' -> '6'
        'B' -> '7'
        'C' -> '8'
        'D' -> 'B'
  'L' -> if n`elem`"125AD" then n else
    if isDigit n then intToDigit $ digitToInt n - 1 else 
    if n=='C' then 'B' else 'A'
  'D' -> if n`elem`"59ACD" then n else
    case n of 
      '1' -> '3'
      '2' -> '6'
      '3' -> '7'
      '4' -> '8'
      '6' -> 'A'
      '7' -> 'B'
      '8' -> 'C'
      'B' -> 'D'

process = foldl moveIfPossible

findCombo n (s:[]) = [process n s]
findCombo n (s:ss) = m:findCombo m ss where m = process n s

main = print $ findCombo '5' ll
